import React, {
  useEffect,
  useState,
  useRef,
  useImperativeHandle,
  useMemo,
  forwardRef,
} from 'react';
// eslint-disable-next-line no-unused-vars
import { Props } from './types';

import Chart from 'chart.js';
import merge from 'lodash/merge';
import assign from 'lodash/assign';
import find from 'lodash/find';

const ChartComponent = forwardRef<Chart | undefined, Props>((props, ref) => {
  const {
    id,
    height = 150,
    width = 300,
    redraw = false,
    type,
    data,
    options = {},
    plugins = [],
  } = props;

  const canvas = useRef<HTMLCanvasElement>(null);

  const computedData = useMemo<Chart.ChartData>(() => {
    if (typeof data === 'function') {
      return canvas.current ? data(canvas.current) : {};
    } else return merge({}, data);
  }, [data, canvas.current]);

  const [chart, setChart] = useState<Chart>();

  useImperativeHandle<Chart | undefined, Chart | undefined>(ref, () => chart, [
    chart,
  ]);

  const renderChart = () => {
    if (!canvas.current) return;

    setChart(
      new Chart(canvas.current, {
        type,
        data: computedData,
        options,
        plugins,
      })
    );
  };

  const onClick = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!chart) return;

    const { getDatasetAtEvent, getElementAtEvent, getElementsAtEvent } = props;

    getDatasetAtEvent && getDatasetAtEvent(chart.getDatasetAtEvent(e), e);
    getElementAtEvent && getElementAtEvent(chart.getElementAtEvent(e), e);
    getElementsAtEvent && getElementsAtEvent(chart.getElementsAtEvent(e), e);
  };

  const updateChart = () => {
    if (!chart) return;

    if (options) {
      chart.options = Chart.helpers.configMerge(chart.options, options);
    }

    if (!chart.config.data) {
      chart.config.data = computedData;
      chart.update();
      return;
    }

    const { datasets: newDataSets = [], ...newChartData } = computedData;
    const { datasets: currentDataSets = [] } = chart.config.data;

    // copy values
    assign(chart.config.data, newChartData);
    chart.config.data.datasets = newDataSets.map(newDataSet => {
      // given the new set, find it's current match
      const currentDataSet = find(
        currentDataSets,
        d => d.label === newDataSet.label && d.type === newDataSet.type
      );

      // There is no original to update, so simply add new one
      if (!currentDataSet || !newDataSet.data) return newDataSet;

      if (!currentDataSet.data) {
        currentDataSet.data = [];
      } else {
        currentDataSet.data.splice(newDataSet.data.length);
      }

      // copy in values
      assign(currentDataSet.data, newDataSet.data);

      // apply dataset changes, but keep copied data
      return {
        ...currentDataSet,
        ...newDataSet,
        data: currentDataSet.data,
      };
    });

    chart.update();
  };

  const destroyChart = () => {
    if (chart) chart.destroy();
  };

  useEffect(() => {
    renderChart();

    return () => destroyChart();
  }, []);

  useEffect(() => {
    if (redraw) {
      destroyChart();
      renderChart();
    } else {
      updateChart();
    }
  }, [props, computedData]);

  return (
    <canvas
      height={height}
      width={width}
      ref={canvas}
      id={id}
      onClick={onClick}
      data-testid='canvas'
    />
  );
});

export default ChartComponent;
import React, { forwardRef } from 'react';
// eslint-disable-next-line no-unused-vars
import { Props } from './types';
import ChartComponent from './chart';

export const Line = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='line' ref={ref} />
));

export const Bar = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='bar' ref={ref} />
));

export const HorizontalBar = forwardRef<Chart | undefined, Props>(
  (props, ref) => <ChartComponent {...props} type='horizontalBar' ref={ref} />
);

export const Radar = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='radar' ref={ref} />
));

export const Doughnut = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='doughnut' ref={ref} />
));

export const PolarArea = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='polarArea' ref={ref} />
));

export const Bubble = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='bubble' ref={ref} />
));

export const Pie = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='pie' ref={ref} />
));

export const Scatter = forwardRef<Chart | undefined, Props>((props, ref) => (
  <ChartComponent {...props} type='scatter' ref={ref} />
));

export default ChartComponent;
